mapper.select:
SELECT t.id, t.business_id, t.function_id, t.approve_operation_id, t.submit_user, u2.user_name AS submitUserName, t.submit_time, t.approve_user, u3.user_name AS approveUserName, t.approve_time, t.approve_state, d.dict_value approveStateName, t.approve_opinion, t.confirm_state, d1.dict_value confirmStateName, t.confirm_user, u4.user_name AS confirmUserName, t.confirm_time, t.confirm_opinion, t.create_by, t.create_time, t.update_by, t.update_time, t.remark FROM confirm_operation_flow t LEFT JOIN sys_user u2 ON u2.user_login = t.submit_user LEFT JOIN sys_user u3 ON u3.user_login = t.approve_user LEFT JOIN sys_user u4 ON u4.user_login = t.confirm_user LEFT JOIN sys_dict_data d ON d.dict_code = t.approve_state and d.dict_type = 'approve_state' LEFT JOIN sys_dict_data d1 ON d1.dict_code = t.confirm_state and d1.dict_type = 'confirm_state' where 1=1
and t.confirm_time >= #{flow.beginDate}
and t.confirm_time <= #{flow.endDate}
and u2.user_name like concat('%', #{flow.submitUserName}, '%')
and t.confirm_state = #{flow.confirmState}
and t.confirm_user = #{flow.confirmUser}
order by t.confirm_time limit #{pageable.offset}, #{pageable.pageSize}

SELECT count(1)  FROM	confirm_operation_flow t
LEFT JOIN sys_user u2 ON u2.user_login = t.submit_user
where  1=1

    and t.confirm_time     >=  #{beginDate}


    and t.confirm_time     <=  #{endDate}


    and u2.user_name    like concat('%', #{submitUserName}, '%')


    and t.confirm_state    = #{confirmState}


    and t.confirm_user    = #{confirmUser}
