mapper.select:
SELECT t.id, t.confirm_flow_id, t.business_id, t.confirm_user, u4.user_name AS confirmUserName, t.function_id, t.submit_user, u2.user_name AS submitUserName, t.submit_time, t.approve_user, u3.user_name AS approveUserName, t.approve_time, t.approve_opinion FROM confirm_operation_queue t LEFT JOIN sys_user u2 ON u2.user_login = t.submit_user LEFT JOIN sys_user u3 ON u3.user_login = t.approve_user LEFT JOIN sys_user u4 ON u4.user_login = t.confirm_user where 1=1
and t.submit_time >= #{queue.beginDate}
and t.submit_time <= #{queue.endDate}
and u2.user_name like concat('%', #{queue.submitUserName}, '%')
order by t.submit_time limit #{pageable.offset}, #{pageable.pageSize}

SELECT count(1)  FROM	approve_operation_queue t
LEFT JOIN sys_user u2 ON u2.user_login = t.submit_user
where  1=1

    and t.submit_time     >=  #{beginDate}


    and t.submit_time     <=  #{endDate}


    and u2.user_name    like concat('%', #{submitUserName}, '%')
