mapper.select:
SELECT t.id, t.factory_code, f.`name` as factoryName, t.duty_date, t.duty_code, d.dict_value as dutyName, t.machine_code, d1.dict_value machineName, t.stock_bill_no, t.bill_no, t.grinding_weight, t.machine_weight, t.different_weight, t.create_by, u.user_name AS createByName, t.create_time, t.update_by, u1.user_name AS updateByName, t.update_time, t.remark FROM material_dosage t left join sys_factory f on f.code = t.factory_code left join sys_dict_data d on d.dict_code = t.duty_code and d.dict_type = 'duty' LEFT JOIN sys_user u ON u.user_login = t.create_by LEFT JOIN sys_user u1 ON u1.user_login = t.update_by LEFT JOIN sys_dict_data d1 ON d1.dict_code = t.machine_code and d1.dict_type = 'machine_code' where 1=1
and t.factory_code = #{info.factoryCode}
and t.duty_date >= #{info.beginDate}
and t.duty_date <= #{info.endDate}
and t.duty_code =#{info.dutyCode}
and t.machine_code like concat('%', #{info.machineCode}, '%')
and t.bill_no like concat('%', #{info.billNo}, '%')
and t.stock_bill_no like concat('%', #{info.stockBillNo}, '%')
order by t.factory_code, t.duty_date limit #{pageable.offset}, #{pageable.pageSize}

SELECT  count(1)   FROM	material_dosage t   where 1=1

    and t.factory_code    = #{factoryCode}


    and t.duty_date    >=  #{beginDate}


    and t.duty_date    <=  #{endDate}


    and t.duty_code   =#{dutyCode}


    and t.machine_code  like concat('%',  #{machineCode} , '%')


    and t.bill_no    like concat('%', #{billNo}, '%')


    and t.stock_bill_no    like concat('%', #{stockBillNo}, '%')

select
IFNULL(sum(t.grinding_weight),0) as grindingWeightToll,
IFNULL(sum(machine_weight),0) as machineWeightToll,
IFNULL(sum(different_weight),0) as differentWeightToll
 from  material_dosage  t
where  1=1

    and t.factory_code    = #{factoryCode}


    and t.duty_date    >=  #{beginDate}


    and t.duty_date    <=  #{endDate}


    and t.duty_code   =#{dutyCode}


    and t.machine_code  like concat('%',  #{machineCode} , '%')


    and t.bill_no    like concat('%', #{billNo}, '%')


    and t.stock_bill_no    like concat('%', #{stockBillNo}, '%')
