mapper.select:
select distinct t.material_code from material_inventory t LEFT JOIN material_info m ON m.CODE = t.material_code where 1=1
and t.material_code like concat('%', #{stock.materialCode}, '%')
and m.name like concat('%', #{stock.materialName}, '%')
and t.stock_code = #{stock.stockCode}
order by t.material_code limit #{pageable.offset}, #{pageable.pageSize}

select t.id,t.material_code, m.name as materialName, m.model, d2.dict_value modelName, m.unit, d3.dict_value unitName, t.stock_code,t.inventory_num,t.remark from material_inventory t LEFT JOIN material_info m ON m.CODE = t.material_code LEFT JOIN sys_dict_data d2 ON d2.dict_code = m.model and d2.dict_type = 'material_model' LEFT JOIN sys_dict_data d3 ON d3.dict_code = m.unit and d3.dict_type = 'material_unit' where t.material_code in
#{code}
order by t.material_code

mapper.update:
update material_inventory  t
set t.inventory_num = t.inventory_num + #{num}
where t.material_code = #{materialCode} and  t.stock_code = #{code}

update material_inventory  t
set t.inventory_num = t.inventory_num  - #{num}
where t.material_code = #{materialCode} and  t.stock_code = #{code}
