mapper.select:
select distinct t.material_code from material_inventory t LEFT JOIN material_info m ON m.CODE = t.material_code where 1=1
and t.material_code like concat('%', #{stock.materialCode}, '%')
and m.name like concat('%', #{stock.materialName}, '%')
and t.stock_code = #{stock.stockCode}
order by t.material_code
limit #{pageable.offset}, #{pageable.pageSize}

select t.id,t.material_code, m.name as materialName, m.model, d2.dict_value modelName, m.unit, d3.dict_value unitName, t.stock_code,t.inventory_num, t.last_update_time, t.remark from material_inventory t LEFT JOIN material_info m ON m.CODE = t.material_code LEFT JOIN sys_dict_data d2 ON d2.dict_code = m.model and d2.dict_type = 'material_model' LEFT JOIN sys_dict_data d3 ON d3.dict_code = m.unit and d3.dict_type = 'material_unit' where t.material_code in
#{code}
and t.stock_code = #{stockCode}
order by t.material_code

select count(1) from  	material_inventory t,	material_info m
WHERE t.material_code = m.code

    and m.code    like concat('%', #{materialCode}, '%')


    and m.name    like concat('%', #{materialName}, '%')


    and t.stock_code    = #{stockCode}


    and m.type    = #{materialType}

SELECT m.CODE as materialCode, m.NAME as materialName, m.model, d1.dict_value as modelName, m.unit, t.stock_code, d.dict_value as unitName, t.inventory_num FROM material_inventory t, material_info m left join sys_dict_data d on d.dict_code = m.model and d.dict_type = 'material_model' left join sys_dict_data d1 on d1.dict_code = m.unit and d1.dict_type = 'material_unit' WHERE t.material_code = m.code
and m.code like concat('%', #{stock.materialCode}, '%')
and m.name like concat('%', #{stock.materialName}, '%')
and t.stock_code = #{stock.stockCode}
and m.type = #{stock.materialType}
order by m.code
