mapper.select:
SELECT t.id, t.material_code, m.name as materialName, m.model, d2.dict_value modelName, m.unit, d3.dict_value unitName, t.unit_price, t.supplyer_code, s.name as supplyerName, t.in_code, t.bill_no, t.material_buyer, u2.user_name AS materialBuyerName, t.material_buytime, t.approve_user, u3.user_name AS approveUserName, t.approve_state, d1.dict_value as approveStateName, t.approve_opinion, t.approve_time, t.bill_state, t.count, t.toll_amount , t.create_by, u.user_name AS createByName, t.create_time, t.update_by, u1.user_name AS updateByName, t.update_time, t.remark FROM raw_material_income t LEFT JOIN sys_user u ON u.user_login = t.create_by LEFT JOIN sys_user u1 ON u1.user_login = t.update_by LEFT JOIN sys_user u2 ON u2.user_login = t.material_buyer LEFT JOIN sys_user u3 ON u3.user_login = t.approve_user left join material_info m on m.code = t.material_code left join customer_supply s on s.code = t.supplyer_code LEFT JOIN sys_dict_data d2 ON d2.dict_code = m.model and d2.dict_type = 'material_model' LEFT JOIN sys_dict_data d3 ON d3.dict_code = m.unit and d3.dict_type = 'material_unit' LEFT JOIN sys_dict_data d1 ON d1.dict_code = t.approve_state AND d1.dict_type = 'approve_state' where 1=1
and t.material_code like concat('%', #{raw.materialCode}, '%')
and m.name like concat('%', #{raw.materialName}, '%')
and s.name like concat('%', #{raw.supplyerName}, '%')
and t.bill_no like concat('%', #{raw.billNo}, '%')
and t.approve_state = #{raw.approveState}
and t.in_code = #{raw.inCode}
order by t.id desc limit #{pageable.offset}, #{pageable.pageSize}
