mapper.select:
SELECT t.id, t.material_code, m.NAME AS materialName, m.model, d2.dict_value modelName, m.unit, d3.dict_value unitName, t.rebuild_count, t.rebuild_date, t.in_code, t.duty_code, d4.dict_value dutyName, t.machine_code, d5.dict_value as machineName, t.bill_no, t.bill_state , t.create_by, u.user_name AS createByName, t.create_time, t.update_by, u1.user_name AS updateByName, t.update_time, t.remark FROM rebuild_inbound t LEFT JOIN sys_user u ON u.user_login = t.create_by LEFT JOIN sys_user u1 ON u1.user_login = t.update_by LEFT JOIN material_info m ON m.CODE = t.material_code LEFT JOIN sys_dict_data d2 ON d2.dict_code = m.model and d2.dict_type = 'material_model' LEFT JOIN sys_dict_data d3 ON d3.dict_code = m.unit and d3.dict_type = 'material_unit' LEFT JOIN sys_dict_data d4 ON d4.dict_code = t.duty_code and d4.dict_type = 'duty' LEFT JOIN sys_dict_data d5 ON d5.dict_code = t.machine_code and d5.dict_type = 'machine_code' where 1=1
and t.material_code like concat('%', #{rebuild.materialCode}, '%')
and m.name like concat('%', #{rebuild.materialName}, '%')
and t.rebuild_date >= #{rebuild.beginDate}
and t.rebuild_date <= #{rebuild.endDate}
and t.in_code = #{rebuild.inCode}
and t.duty_code = #{rebuild.dutyCode}
and t.machine_code = #{rebuild.machineCode}
order by t.id desc limit #{pageable.offset}, #{pageable.pageSize}

SELECT count(1)  FROM	rebuild_inbound t
LEFT JOIN material_info m ON m.CODE = t.material_code
where  1=1

    and t.material_code    like concat('%', #{materialCode}, '%')


    and m.name    like concat('%', #{materialName}, '%')


    and t.rebuild_date     >=  #{beginDate}


    and t.rebuild_date     <=  #{endDate}


    and t.in_code   = #{inCode}


    and t.duty_code   = #{dutyCode}


    and t.machine_code   = #{machineCode}
