mapper.select:
SELECT t.id, t.sale_d, t.customer_code, c.name as customerName, t.material_code, m.NAME AS materialName, m.model, d2.dict_value modelName, m.unit, d3.dict_value unitName, t.unit_price, t.sale_date, t.sale_count, t.toll_amount, t.create_by, u.user_name AS createByName, t.create_time, t.update_by, u1.user_name AS updateByName, t.update_time, t.remark FROM sales_coustomer_pay t LEFT JOIN sys_user u ON u.user_login = t.create_by LEFT JOIN sys_user u1 ON u1.user_login = t.update_by LEFT JOIN material_info m ON m.CODE = t.material_code LEFT JOIN sys_dict_data d2 ON d2.dict_code = m.model and d2.dict_type = 'material_model' LEFT JOIN sys_dict_data d3 ON d3.dict_code = m.unit and d3.dict_type = 'material_unit' left join customer_supply c on c.code = t.customer_code where 1=1
and m.name like concat('%', #{pay.materialName}, '%')
and c.name like concat('%', #{pay.customerName}, '%')
and t.sale_date >= #{pay.beginDate}
and t.sale_date <= #{pay.endDate}
order by t.id limit #{pageable.offset}, #{pageable.pageSize}

SELECT count(1) FROM sales_coustomer_pay t LEFT JOIN material_info m ON m.CODE = t.material_code left join customer_supply c on c.code = t.customer_code where 1=1
and m.name like concat('%', #{materialName}, '%')
and c.name like concat('%', #{customerName}, '%')
and t.sale_date >= #{beginDate}
and t.sale_date <= #{endDate}
