mapper.select:
SELECT t.id, t.customer_code, c.name as customerName, t.material_code, m.NAME AS materialName, m.model, d2.dict_value modelName, m.unit, d3.dict_value unitName, t.unit_price, t.return_count, t.toll_amount, t.approve_state, d4.dict_value as approveStateName, t.approve_opinion, t.approve_time, t.confirm_state, d5.dict_value as confirmStateName, t.confirm_opinion, t.confirm_time, t.bill_no, t.bill_state , t.in_code, t.return_type, t.return_user, u2.user_name AS returnUserName, t.return_time, t.create_by, u.user_name AS createByName, t.create_time, t.update_by, u1.user_name AS updateByName, t.update_time, t.remark FROM sales_return t LEFT JOIN sys_user u ON u.user_login = t.create_by LEFT JOIN sys_user u1 ON u1.user_login = t.update_by LEFT JOIN sys_user u2 ON u2.user_login = t.return_user LEFT JOIN material_info m ON m.CODE = t.material_code LEFT JOIN sys_dict_data d2 ON d2.dict_code = m.model and d2.dict_type = 'material_model' LEFT JOIN sys_dict_data d3 ON d3.dict_code = m.unit and d3.dict_type = 'material_unit' LEFT JOIN sys_dict_data d4 ON d4.dict_code = t.approve_state and d4.dict_type = 'approve_state' LEFT JOIN sys_dict_data d5 ON d5.dict_code = t.confirm_state and d5.dict_type = 'confirm_state' left join customer_supply c on c.code = t.customer_code where 1=1
and t.material_code like concat('%', #{return.materialCode}, '%')
and m.name like concat('%', #{return.materialName}, '%')
and t.return_time >= #{return.beginDate}
and t.return_time <= #{return.endDate}
and u2.user_name like concat('%', #{return.returnUserName}, '%')
and t.bill_no like concat('%', #{return.billNo}, '%')
and t.approve_state = #{return.approveState}
and t.in_code = #{return.inCode}
and t.retutn_type = #{return.retutnType}
order by t.return_time desc limit #{pageable.offset}, #{pageable.pageSize}

SELECT  count(1)    FROM	sales_return t
LEFT JOIN material_info m ON m.CODE = t.material_code
LEFT JOIN sys_user u2 ON u2.user_login = t.return_user
where  1=1

    and t.material_code    like concat('%', #{materialCode}, '%')


    and m.name    like concat('%', #{materialName}, '%')


    and t.return_time     >=  #{beginDate}


    and t.return_time     <=  #{endDate}


    and u2.user_name   like concat('%', #{returnUserName}, '%')


    and t.bill_no   like concat('%', #{billNo}, '%')


    and t.approve_state   = #{approveState}


    and t.in_code   = #{inCode}


    and t.retutn_type   = #{retutnType}
