mapper.select:
SELECT t.id, t.function_id, t.operation_type, t.user_code, u.user_name as userName, t.operation_time, t.operation_info, t.operation_result, t.operation_msg, t.login_ip FROM sys_log t left join sys_user u on u.user_login = t.user_code where 1=1
and t.operation_info like concat('%', #{log.operationInfo}, '%')
and t.operation_result like concat('%', #{log.operationResult}, '%')
and t.operation_time >= #{log.beginDate}
and t.operation_time <= #{log.endDate}
and u.user_name like concat('%', #{log.userName}, '%')
order by t.operation_time desc limit #{pageable.offset}, #{pageable.pageSize}

SELECT  count(1)  FROM	sys_log t
left join sys_user  u  on  u.user_login = t.user_code
where 1=1

    and t.operation_info    like concat('%', #{operationInfo}, '%')


    and t.operation_result    = #{operationResult}


    and t.operation_time    >=  #{beginDate}


    and t.operation_time    <=  #{endDate}


    and u.user_name    like concat('%', #{userName}, '%')
