mapper.select:
SELECT t.id, t.user_id, t.user_login, t.user_name, t.user_type, da2.dict_value userTypeName, t.role_id, r.role_name as roleName, t.dept_id, t.email, t.weChat_no, t.phone_no, t.sex, da.dict_value sexName, t.last_password_date, t.STATUS, da1.dict_value as statusName, t.login_ip, t.login_date, t.order_num, t.urgent_name, t.urgent_phone, t.entry_date, t.leave_date, t.create_by, u.user_name AS createByName, t.create_time, t.update_by, u1.user_name AS updateByName, t.update_time, t.remark FROM sys_user t LEFT JOIN sys_dict_data da ON da.dict_code = t.sex and da.dict_type = 'sex' LEFT JOIN sys_dict_data da1 ON da1.dict_code = t.`status` and da1.dict_type = 'user_status' LEFT JOIN sys_dict_data da2 ON da2.dict_code = t.user_type and da2.dict_type = 'user_type' LEFT JOIN sys_user u ON u.user_login = t.create_by LEFT JOIN sys_user u1 ON u1.user_login = t.update_by left join sys_role r on t.role_id = r.role_id where 1=1
and t.user_id like concat('%', #{user.userId}, '%')
and t.user_login like concat('%', #{user.userLogin}, '%')
and t.user_name like concat('%', #{user.userName}, '%')
and t.user_type = #{user.userType}
and t.dept_id = #{user.deptId}
order by t.user_id limit #{pageable.offset}, #{pageable.pageSize}

SELECT  count(1)    FROM   sys_user t
where 1=1

    and t.user_id    like concat('%', #{ userId}, '%')


    and t.user_login    like concat('%', #{userLogin}, '%')


    and t.user_name    like concat('%', #{ userName}, '%')


    and t.user_type   = #{ userType}


    and t.dept_id   = #{deptId}


    and t.sex   = #{sex}
